mapper.select:
select count(1)
from t_member
where regTime < #{month}

select count(1)
from t_member
where regTime = #{today}

select count(1)
from t_member
where regTime >= #{thisWeekMonday}

select id, fileNumber, name, sex, idCard, phoneNumber, regTime, password, email, birthday, remark, healthmanager from t_member
and id = #{id}
and name = #{name}
and healthmanager = #{healthmanager}
